public class NegativeNumToPositiveNum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number = -150;
		int positiveNumber;
		if(number<0) {
			 positiveNumber = number * -1;
			 System.out.println(positiveNumber); 
		}
     
	}

}
